import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import * as API from '../services/pokemons.js';
import { POKE_IMAGE_BASE } from '../constants.js';

const Grid = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gridGap: '12px'
  };

const Item = {
    minHeight: '200px',
    backgroundColor: '#eee'
  };

export default function PokemonList() {
    const [pokemons, setPokemons] = useState([])

    useEffect(() => {
        API.getKantoPokemons().then(setPokemons)
    }, [])

    return (
        <div style={{'display': 'flex', 'margin': 'auto'}}>
            {pokemons.results != null && pokemons.results.map(p => PokemonCard(p))}
        </div>
    );
}

const PokemonCard = (pokemon) =>
    <div className='Item'>
        <Link style={{textDecoration: 'none', color: 'inherit'}} key={pokemon.name} to={`pokemon/${pokemon.url.split('/')[pokemon.url.split('/').length - 2]}`}>
            <img
                width={230}
                height={230}
                alt={pokemon.name}
                key={pokemon.name}
                src={`${POKE_IMAGE_BASE}${pokemon.name}.jpg`}
            />
            <h5 style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>{pokemon.name}</h5>
        </Link>
    </div>
